sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    //Instantiate the getEmailAddressAsync Script Include
    var getEmailAddr = new GlideAjax('lookupEmailAddressAjax');

    //specify the getEmail function
    getEmailAddr.addParam('sysparm_name', 'getEmail');


    if (isLoading || newValue === '') {
        if (g_form.isNewRecord() && (g_form.getValue('u_requested_for') != null)) {
            //Pass the requested_for record sys_id
            getEmailAddr.addParam('sysparm_userRecId', g_form.getValue('u_requested_for'));

            //send the request to the server
            getEmailAddr.getXMLAnswer(populateEmailField);
        }
        return;
    }


    //Pass the requested_for record sys_id
    getEmailAddr.addParam('sysparm_userRecId', g_form.getValue('u_requested_for'));

    //send the request to the server
    getEmailAddr.getXMLAnswer(populateEmailField);



    //when reponse is back from server
    function populateEmailField(answer) {

		var email = answer ? answer : " --- email address not found ---";		
		g_form.setValue('u_requested_for_email', email);

    }



}
