sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {

		//If existing record, return on the isLoading for existing
		if (!g_form.isNewRecord()) {
			return;
		}

    }



    var ga = new GlideAjax('RQ_ChoiceListAjax');
    ga.addParam('sysparm_name', 'getRequestChoices');

    ga.addParam('sysparm_rqType', g_form.getValue('u_request_type'));
    ga.getXMLAnswer(updateWhatNeeded);

    function updateWhatNeeded(answer) {

        var list = JSON.parse(answer);
        g_form.clearOptions('u_what_needed');

        for (var i = 0; i < list.length; i++) {
            var opt = list[i];
            g_form.addOption('u_what_needed', opt.value, opt.label, opt.sequence);
        }


    }


    //Type appropriate comment here, and begin script below

}
