sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    g_form.getReference('u_requested_for', doUpdate);

    //Type appropriate comment here, and begin script below

}

function doUpdate(value1) {
    g_form.setValue('u_requested_for_email', value1.getValue('email'));
	var emailAddr = value1.getValue('email');
	var lastLogin = value1.getValue('last_login');
	var empNumber = value1.getValue('employee_number');
	var gender = value1.getValue('gender');
	var active = value1.getValue('active');

	var summary = " Email: " + emailAddr + ", Last Login:" + lastLogin + ", Emp ID:" + empNumber + ", M/F:" + gender + ", Is Active:" + active + ".";

	var description = g_form.setValue('description', summary );

}
