sys_script_include.script:
var RQ_ChoiceList = Class.create();
RQ_ChoiceList.prototype = {
    initialize: function() {},

    getRequestChoices: function(rqType) {

        var list = [];

        var choiceGr = new GlideRecord('sys_choice');
        choiceGr.addQuery('name', 'x_58872_needit_needit');
        choiceGr.addQuery('element', 'u_what_needed');
		choiceGr.addQuery('dependent_value', rqType);
		choiceGr.addQuery('language', gs.getSession().getLanguage());
		choiceGr.addQuery('inactive', false);
		choiceGr.orderBy('sequence');
		choiceGr.query();

		while (choiceGr.next()) {
			var obj = {
				"label" : choiceGr.getValue('label'),
				"value" : choiceGr.getValue('value'),
				"sequence" : choiceGr.getValue('sequence')
			};

			list.push(obj);

		}         

        return list;
    },

    type: 'RQ_ChoiceList'
};
