sysauto_script.script:
    var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values older
	// than the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var overdueTaskRecord = new GlideRecord('x_58872_needit_requireit_task');
	overdueTaskRecord.addQuery('due_date','<=',rightNow);
	overdueTaskRecord.addQuery('state','!=',3);

	overdueTaskRecord.query();
	// Write a log message for each overdue NeedIt Task Record
	while(overdueTaskRecord.next()){
		gs.info("Overdue NeedIt Task record = " + overdueTaskRecord.number);
		gs.eventQueue('x_58872_needit.overdueRequireITTask', overdueTaskRecord, overdueTaskRecord.number, gs.getUserName());
		//Event triggers a script action - UpdateRequireIT Overdue Task Priority, basically the event handler

	}
